import axios from 'axios';

const AxiosWithAuth =()=>{
    const token = sessionStorage.getItem("token");

    return axios.create({
        baseURL: "https://african-marketplace-backend.herokuapp.com/api",
        headers: {
            "Content-Type": "application / json",
            Authorization: token
        }
    });
}

export default AxiosWithAuth;
